package com.tgid.tri.auth

import groovy.json.JsonSlurper

class StateService {

    void seedStates() {
        def states = new JsonSlurper().parseText(stateJson)
        states.each {
            try {
                State.findOrSaveWhere(abbrev: it.Abbrev.trim(), provID: it.StateProvID.trim(), countryID: it.CountryID.trim(), name: it.StateProvName.trim())
            } catch(Exception e) {
                log.error e
            }
        }
    }

